export const initialState = {
  isLoggingIn: false,
  isLoggingOut: false,
  isLoggedIn: false,
  signUpData: {},
  loginData: {},
  me: null,
};

const dummyUser = {
  id: 1,
  nickname: 'MJoonJung',
  Posts: [],
  Followings: [],
  Followers: [],
};

export const SIGN_UP = 'SIGN_UP';
export const SIGN_UP_SUCCESS = 'SIGN_UP_SUCCESS';

export const signUpAction = (data) => {
  return {
    type: SIGN_UP,
    data,
  };
};

export const signUpSuccess = {
  type: SIGN_UP_SUCCESS,
};

export const loginRequestAction = (data) => {
  return {
    type: 'LOG_IN_REQUEST',
    data,
  }
};
export const loginSuccessAction = (data) => {
  return {
    type: 'LOG_IN_SUCCESS',
    data,
  }
};
export const loginFailureAction = (data) => {
  return {
    type: 'LOG_IN_FAILURE',
    data,
  }
};
export const logoutRequestAction = (data) => {
  return {
    type: 'LOG_OUT_REQUEST',
    data,
  }
};
export const logoutSuccessAction = () => {
  return {
    type: 'LOG_OUT_SUCCESS',
  }
};
export const logoutFailureAction = () => {
  return {
    type: 'LOG_OUT_FAILURE',
  }
};
export const signUp = () => {
  return {
    type: SIGN_UP,
  }
};

export default (state = initialState, action) => {
  switch (action.type) {
    case 'LOG_IN_REQUEST': {
      console.log('reducer login')
      return {
        ...state,
        isLoggingIn: true,
      };
    }
    case 'LOG_IN_SUCCESS': {
      return {
        ...state,
        isLoggingIn: false,
        isLoggedIn: true,
        me: dummyUser,
      };
    }
    case 'LOG_IN_FAILURE': {
      return {
        ...state,
        isLoggingIn: false,
      };
    }
    case 'LOG_OUT_REQUEST': {
      return {
        ...state,
        isLoggingOut: true,
      };
    }
    case 'LOG_OUT_SUCCESS': {
      return {
        ...state,
        isLoggingOut: false,
        isLoggedIn: false,
        me: null,
      };
    }
    case 'LOG_OUT_FAILURE': {
      return {
        ...state,
        isLoggingOut: false,
      };
    }
    case 'SIGN_UP': {
      return {
        ...state,
        signUpData: action.data,
      };
    }
    default: {
      return {
        ...state,
      }
    }
  }
};
